import React from "react";


const Greeting = () =>{
    const date = new Date();
    const time = date.getHours();
    var greeting = "";
    const style = {

    }
    if(time < 12){
        greeting = "Good Morning";
        style.color = 'Blue'
    }else if(time < 19){
        greeting = "Good AfterNoon";
        style.color = 'orange'
    }else{
        greeting = "Good Night";
        style.color = 'black'
    }

    return(
        <h1>Hello Sir,<span style = {style}>{greeting}</span></h1>
    )
};

export default Greeting;
