import React, { useState } from "react";

const App=()=>{
    const[name,setName] = useState();
    var name1 = '';

    const change = (event) => {
         name1 = event.target.value;
    }

    const clicks = () => {
        setName(name1);
    }


    return(
        <>
            <div>
                <h1>Hello {name} </h1>
                <input type="text" placeholder="Enter your name" onChange = {change}></input>
                <br></br>
                <button onClick = {clicks}>Click me</button>
            </div>
        </>
    )

};

export default App;
