import React, { useState } from "react";

const App=()=>{
    var time = new Date().toLocaleTimeString();
    const[ctime,setctame] = useState(time);

    const clicks = () => {
        time = new Date().toLocaleTimeString();
        setctame(time);
    }

    setInterval(clicks,1000);
   

    return(
        <>
            <div>
                <h1 onClick = {clicks}> </h1>
             
            </div>
        </>
    )

};

export default App;
