import React from "react";
import ReactDom from "react-dom";
import "./index.css";
var date = new Date();
var time = date.getHours();
var greeting = "";

const style = {
  color:"black"
};

if(time < 12){
  greeting = 'Good Morning';
  style.color = "red";

}else if(time < 19){
  greeting = "Good AfterNoon";
  style.color = "green";
}else{
  greeting = "Good Night";
  style.color = "blue";
}

ReactDom.render(
  <>
    <h1>hello Sir,<span style = {style}>{greeting}</span> </h1>


  </>,document.getElementById("root")
);
